import './ExpensesFilter.css';
import { useState } from 'react';

const ExpensesFilter = (props) => {

    const [ y22 , setYear22 ] = useState()
    const [ y21, setYear21 ] = useState('');
    const [ y20, setYear20 ] = useState('');
    const [ y19, setYear19 ] = useState('');

    const clickHandler = (event) => {
        setYear22(event.target.value)
        const enteredYear = event.target.value
        props.selectedYear(enteredYear)
    }
    
  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select onClick={clickHandler}>
          <option value={y22}></option>
          <option value={y21}>2021</option>
          <option value={y20}>2020</option>
          <option value={y19}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
