import './ExpensesFilter.css';
import { useState } from 'react';

const ExpensesFilter = (props) => {
    const [ setYear ] = useState()
    const clickHandler = (event) => {
        setYear(event.target.value)
        const enteredYear = event.target.value
        console.log(enteredYear)
        props.selectedYear(enteredYear)
    }
  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option value='2022' onClick={clickHandler}>2022</option>
          <option value='2021' onClick={clickHandler}>2021</option>
          <option value='2020' onClick={clickHandler}>2020</option>
          <option value='2019' onClick={clickHandler}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
